splash = null;
logo = null;
logoSize = 200;


pc.script.createLoadingScreen(function (app) {
    var showSplash = function () {
        // splash wrapper
        var wrapper = document.createElement('div');
        wrapper.id = 'application-splash-wrapper';
        document.body.appendChild(wrapper);

        // splash
        splash = document.createElement('div');
        splash.id = 'application-splash';
        wrapper.appendChild(splash);
        splash.style.display = 'none';

        logo = document.createElement('img');
        
        logo.src = 'data:image/png;base64,';
        //splash.appendChild(logo);
            //logo.style.align = "center";
            logo.style.margin = "0px auto";
            logo.style.position = 'absolute';
            logo.style.height = logoSize+ 'px';
            logo.style.width = logoSize+'px';
            //logo.style.left = '43px';
            //logo.style.top = '-230px';
        //logo.style.position = 'absolute';
            //logo.style.left = '173px';
            //logo.style.top = '100px';
            //logo.style.margin = "0 auto";
        
        logo.onload = function () {
            /*logo.style.height = '50%';
            logo.style.width = '50%';
            logo.style.left = '90%';
            logo.style.top = '50%';
            logo.style.marginleft ='auto';
            logo.style.marginright= 'auto';
            logo.style.margin = "0 auto";
            logo.style.align = "middle";*/
            splash.style.display = 'block';//'block';
            logo.style.display = 'block';
        };
            //splash.style.display = 'block';

        var container = document.createElement('div');
        container.id = 'progress-bar-container';
        splash.appendChild(container);

        //logo.style.top = '30%';
        document.body.appendChild(logo);
        
        var bar = document.createElement('div');
        bar.id = 'progress-bar';
        container.appendChild(bar);

    };
    
    
    var updateLogo = function () {
	    var width = window.innerWidth;
	    var height = window.innerHeight;
	    logo.style.left = (width-logoSize)*0.5 + "px";
	    logo.style.top = (height-logoSize - 100)*0.5 + "px";
    };
    

    var hideSplash = function () {
        var splash = document.getElementById('application-splash-wrapper');
        splash.parentElement.removeChild(splash);
        logo.parentElement.removeChild(logo);
    };

    var setProgress = function (value) {
        updateLogo();
        
        var bar = document.getElementById('progress-bar');
        if(bar) {
            value = Math.min(1, Math.max(0, value));
            bar.style.width = value * 100 + '%';
        }
    };

    var createCss = function () {
        var css = [
            'body {',
            '    background-color: #000000;',
            '}',

            '#application-splash-wrapper {',
            '    position: absolute;',
            '    top: 0;',
            '    left: 0;',
            '    height: 100%;',
            '    width: 100%;',
            '    background-color: #000000;',
            '}',

            '#application-splash {',
            '    position: absolute;',
            '    top: calc(50% + 128px);',
            '    width: 264px;',
            '    left: calc(50% - 132px);',
            '}',

            '#application-splash img {',
            '    width: 100%;',
            '}',

            '#progress-bar-container {',
            '    margin: 20px auto 0 auto;',
            '    height: 5px;',
            '    width: 100%;',
            '    background-color: #2f4268;',
            '}',

            '#progress-bar {',
            '    width: 0%;',
            '    height: 100%;',
            '    background-color: #FFFFFF;',
            '}',
            '@media (max-width: 480px) {',
            '    #application-splash {',
            '        width: 170px;',
            '        left: calc(50% - 85px);',
            '    }',
            '}'
        ].join("\n");

        var style = document.createElement('style');
        style.type = 'text/css';
        if (style.styleSheet) {
          style.styleSheet.cssText = css;
        } else {
          style.appendChild(document.createTextNode(css));
        }

        document.head.appendChild(style);
    };


    createCss();

    showSplash();
    updateLogo();
        
    app.on('preload:end', function () {
        app.off('preload:progress');
    });
    app.on('preload:progress', setProgress);
    app.on('start', hideSplash);
});